import React, {Component} from "react"; 
import { Card, Form, Col, Button } from "react-bootstrap";

class MessageMe extends Component {
  state = { 
    senderName: "",
    senderEmail: "",
    senderMessage: ""
  }

  updateMessageFields = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

 handleSubmit (event) {
        event.preventDefault()

        const {
          USER_ID: user
        } = this.props.env
                
          this.sendMessage(
          this.state.senderEmail,
          this.state.senderMessage,
          this.state.senderName,
          user
        )

        this.setState({
          formSubmitted: true
        })
      }

       sendMessage (senderEmail, senderMessage, user) {
        window.emailjs.send(
          'default_service', // default email provider in your EmailJS account
          
          {
            senderEmail,
            senderMessage
          },
          user
        )
          .then(res => {
            this.setState({ formEmailSent: true })
          })
          // Handle errors here however you like, or use a React error boundary
          .catch(err => console.error('Failed to send feedback. Error: ', err))
      }

  render(){

    return(
        <div className = "messageMeContainer">
          <Card className = "messageInfo">    <h3 className ="contactCardHead">Message Me</h3> 
            <Form className = "messageForm" onSubmit={this.handleSubmit}>
              <Form.Row>
                <Form.Group as={Col} controlId="formGridName">
                  <Form.Label>Name</Form.Label>
                  <input type="Name" 
                  id="name"
                  name="senderName"
                  onChange={this.handleChange}
                  placeholder="Enter your name here"
                  required
                  value={senderName}
                  />

                  </Form.Group> <Form.Group as={Col} controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <input type="email" placeholder="Enter email address" />
                  </Form.Group>
                </Form.Row>

                  <Form.Group controlId="formGridMessage">
                    <Form.Label>Message</Form.Label>
                    <textarea 
                    id="message"
                    name="senderMessage"
                    onChange={this.handleChange}
                    placeholder="Enter your message here"
                    required
                    value={senderMessage}/>
                  </Form.Group>
                  <div className="btn-group">
                  <button className="btn btn--cancel" onClick={handleClose}>
                    Cancel
                  </button>
                <button variant="primary" type="submit" value="submit" className="btn btn--submit">
                  Send
                </button>
                </div>
              </Form>
            </Card>
        </div>

    )
}
}

export default MessageMe;
