import React, {Component} from "react"; 
import { Card, Form, Col, Button } from "react-bootstrap";

class MessageMe extends Component {
  state = { 
    name: "",
    email: "",
    message: ""
  }

  componentDidMount(){this.updateMessageFields()};

  updateMessageFields = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };
  

  render(){



    return(
        <div className = "messageMeContainer">
          <Card className = "messageInfo">    <h3 className ="contactCardHead">Message Me</h3> 
            <Form>
              <Form.Row>
                <Form.Group as={Col} controlId="formGridName">
                  <Form.Label>Name</Form.Label>
                  <input type="Name" placeholder="Name" />

                  </Form.Group> <Form.Group as={Col} controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <input type="email" placeholder="Enter email address" />
                  </Form.Group>
                </Form.Row>

                  <Form.Group controlId="formGridMessage">
                    <Form.Label>Message</Form.Label>
                    <textarea placeholder="Type your message here" />
                  </Form.Group>

                <Button variant="primary" type="submit">
                  Send
                </Button>
              </Form>
            </Card>
        </div>

    )
}
}

export default MessageMe;
